const fs = require('fs-extra');
const path = require('path');
let clc = require('cli-color');

let ROOT = path.resolve(process.cwd());
let PRE_PROCESSED_FOLDER = path.join(ROOT, '/app/Resources/views/pre-processed');
let UI_COMPONENTS_FOLDER = path.join(ROOT, '/app/Resources/views/ui-components');

try {
    fs.emptyDirSync(PRE_PROCESSED_FOLDER);
    fs.emptyDirSync(UI_COMPONENTS_FOLDER);

    fs.ensureFileSync(PRE_PROCESSED_FOLDER + '/.gitkeep');
    console.info(clc.green('Pre-processed and ui-components folders have been cleared'));
}
catch(err) {
    console.error(err);
}
