// COPY PROJECT BASE JS FILES

var fse = require('fs-extra');
var path = require('path');

// path consts
var ROOT = path.resolve(process.cwd());

// main js
var BASE_JS_FOLDER = path.join(ROOT, 'node_modules/ui-project-base/js/');
var BASE_JS_DEST_FOLDER = path.join(ROOT, 'web/js/');

// service workers -
// these should be copied into the root due to SW scoping
var BASE_SW_FOLDER = path.join(ROOT, 'node_modules/ui-project-base/service-workers/');
var BASE_SW_DEST_FOLDER = path.join(ROOT, 'web/');

// fonts -
var BASE_FONTS_FOLDER = path.join(ROOT, 'node_modules/ui-project-base/fonts/');
var BASE_FONTS_DEST_FOLDER = path.join(ROOT, 'web/assets/fonts');

// copy js folder
fse.copySync(BASE_JS_FOLDER, BASE_JS_DEST_FOLDER);

// copy sw folder
fse.copySync(BASE_SW_FOLDER, BASE_SW_DEST_FOLDER);

// copy fonts folder
fse.copySync(BASE_FONTS_FOLDER, BASE_FONTS_DEST_FOLDER);
