(function(){
  // set global variable for browser level
  window.isModernBrowser = false;

  // array of classes to add to html element
  var classes = ['js', 'is-legacy'];

  // cut the mustard - https://sixtwothree.org/posts/cutting-the-mustard-revisited + CSS.supports to capture Android 4.4
  // 'legacy': <IE10 & Android Webkit<4.4 - no reliable svg, no reliable flexbox, no reliable css animations
  // 'modern': everything else - classlist, requestanimationframe etc;

  if('visibilityState' in document || !!(window.CSS && window.CSS.supports)){
    window.isModernBrowser = true;
    classes = ['js', 'is-modern'];
  }

  // Detect if iOS/Android bridge is present
  if ((typeof SKY_SPORTS_APP !== 'undefined') || (typeof appWrapper !== 'undefined') || (typeof AndroidApp !== 'undefined')) {
    classes.push('sdc-app-bridge-active');
  }

  // add utility classes
  window.sdc = window.sdc || {};

  // mobile is defined as Android and IOS devices, plus Ipad pro which identifies as Mac OS but has touch events
  var isMobile = ('ontouchstart' in document.documentElement) && (/Android|iPhone|iPad|iPod|Mac OS/i.test(navigator.userAgent));

  window.sdc.isDesktop = !isMobile;
  window.sdc.isMobile = isMobile;

  document.head.insertAdjacentHTML('beforeend', '<style>' + (window.sdc.isInApp ? '.ui-is-hidden--in-app' : (isMobile ? '.ui-is-hidden--mobile' : '.ui-is-hidden--desktop')) + '{display: none !important;}</style>');

  Array.prototype.push.apply(classes, [window.sdc.isInApp && 'ui-is-in-app', window.sdc.isDesktop && 'ui-is-desktop', (window.sdc.isMobile && !window.sdc.isInApp) && 'ui-is-mobile'].filter(function(item){return item}));

  // add classes to html element
  document.documentElement.className = classes.join(' ');
}());
