function interpolate(context) {
  var replacements = arguments;
  var replaceIndex = 1;

  return context.replace(/%s/g, function(match) {
    if (!replacements[replaceIndex]) {
      return match;
    }

    var replace = replacements[replaceIndex];
    replaceIndex++;

    return replace;
  });
}

module.exports = interpolate;
