const helper = require('../../js/helpers/interpolate');

describe('Helper (interpolate)', () => {
  it('replaces variables', () => {
    const test = helper('%s should be replaced', 'Variables');
    expect(test).to.equal('Variables should be replaced');
  });

  it('replaces multiple variables', () => {
    const test = helper('%s should be %s', 'Variables', 'replaced');
    expect(test).to.equal('Variables should be replaced');
  });

  it('replaces any number of variables', () => {
    const test = helper(
      '%s %s %s %s %s %s',
      'Variables', 'should', 'be', 'replaced', 'many', 'times'
    );
    expect(test).to.equal('Variables should be replaced many times');
  });

  it('only replaces as many variables as it has', () => {
    const test = helper('%s should %s %s', 'Variables', 'be');
    expect(test).to.equal('Variables should be %s');
  });

  it('only replaces lower case variables', () => {
    const test = helper('%S should %s %s', 'Variables', 'be');
    expect(test).to.equal('%S should Variables be');
  });
});
