const lockBody = require('../js/modules/components/lock-body.js');

describe('Lock body', () => {
  let clock;

  beforeEach(() => {
    document.documentElement.style.cssText = "height: 100%;overflow: auto;";
    document.body.style.height = "1000px";
    clock = sinon.useFakeTimers();
  });

  afterEach(() => {
    document.documentElement.style.cssText = "";
    document.body.style.height = "";
    clock.restore();
  });

  it('is an object', () => {
    expect(typeof lockBody).to.equal('object')
  });

  it('locks the body when true value is passed', () => {
    lockBody.toggleLock(true);
    expect(document.body.style.position).to.equal('fixed');
  });

  it('unlocks the body when false value is passed', () => {
    lockBody.toggleLock(true);
    lockBody.toggleLock();
    expect(document.body.style.position).to.equal('');
  });

})
